import org.springframework.web.method.HandlerMethod;import org.springframework.web.servlet.ModelAndView;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.lang.reflect.Method;public class ViewNameInterceptor extends HandlerInterceptorAdapter {    @Override    public void postHandle(HttpServletRequest req, HttpServletResponse resp, Object handler, ModelAndView mv)    throws Exception {        if (mv != null) {            req.setAttribute("SPRING_VIEW_NAME", mv.getViewName());            if (handler instanceof HandlerMethod) {                HandlerMethod hm = (HandlerMethod) handler;                Method method = hm.getMethod();                req.setAttribute("SPRING_HANDLER_CLASS", method.getDeclaringClass().toString());                req.setAttribute("SPRING_HANDLER_METHOD", method.getName());            }        }        super.postHandle(req, resp, handler, mv);    }}